module.exports = function(grunt) {
  grunt.loadNpmTasks('grunt-jsdoc');
  grunt.loadNpmTasks('grunt-contrib-watch');
  grunt.initConfig({
    watch : {
      files: 'lib/*.js',
      tasks: [ 'jsdoc' ]
    },
    jsdoc : {
      dist : {
        src: ['lib/*.js'],
        options: {
          destination: 'doc',
          private: false,
          lenient: true
        }
      }
    },
    build : {
    }
  });
};
